{
  "ownerApp": "Transaction Router",
  "createTime": 0,
  "updateTime": 0,
  "createdBy": "Philip Porter",
  "updatedBy": "Philip Porter",
  "name": "transaction-workflow",
  "description": "This workflow will trigger two microservices",
  "version": 0,
  "tasks": [
    {
      "name": "basic-microservice-1",
      "taskReferenceName": "task-1",
      "description": "This task will post to the 1st sample microservice",
      "inputParameters": {
      	"http_request": {
      		"uri": "http://localhost:1111/receive",
      		"method": "POST",
      		"body": {
      			"port":"${workflow.input.port}",
      	        "type":"${workflow.input.type}",
      	        "message":"${workflow.input.message}"
      		}
      	}
      },
      "type": "HTTP",
      "startDelay": 0
    },
    {
      "name": "basic-microservice-2",
      "taskReferenceName": "task-2",
      "description": "This task will post to the 2nd sample microservice",
      "inputParameters": {
      	"http_request": {
      		"uri": "http://localhost:2222/receive",
      		"method": "POST",
      		"body": {
      			"port":"${task-1.output.response.body.port}",
      	        "type":"${task-1.output.response.body.type}",
      	        "message":"${task-1.output.response.body.message}",
      	        "trailer":"${task-1.output.response.body.trailer}"
      		}
      	}
      },
      "type": "HTTP",
      "startDelay": 0
    }
  ],
  "schemaVersion": 2
}

{
  "ownerApp": "Transaction Router",
  "createTime": 0,
  "updateTime": 0,
  "createdBy": "Philip Porter",
  "updatedBy": "Philip Porter",
  "name": "transaction-workflow-port-filter",
  "description": "This workflow will trigger two microservices after filtering based on port",
  "version": 0,
  "tasks": [
    {
  "name": "filter-port",
  "taskReferenceName": "filter-port",
  "inputParameters": {
    "port": "${workflow.input.port}"
  },
  "type": "DECISION",
  "caseValueParam": "port",
  "decisionCases": {
    "1111": [{
    	"type": "HTTP",
        "name": "basic-microservice-1",
        "taskReferenceName": "basic-microservice-1",
        "inputParameters": {
          "http_request": {
            "uri": "http://localhost:1111/receive",
            "method": "POST",
            "body": {
              "port": "${workflow.input.port}",
              "type": "${workflow.input.type}",
              "message": "${workflow.input.message}"
            }
          }
        }
      },
      {
      	"type": "HTTP",
        "name": "basic-microservice-2",
        "taskReferenceName": "basic-microservice-2",
        "inputParameters": {
          "http_request": {
            "uri": "http://localhost:2222/receive",
            "method": "POST",
            "body": {
              "port": "${basic-microservice-1.output.response.body.port}",
              "type": "${basic-microservice-1.output.response.body.type}",
              "message": "${basic-microservice-1.output.response.body.message}",
              "trailer": "${basic-microservice-1.output.response.body.trailer}"
            }
          }
        }
      }
    ],
    "2222": [{
    	"type": "HTTP",
        "name": "basic-microservice-1",
        "taskReferenceName": "basic-microservice-1",
        "inputParameters": {
          "http_request": {
            "uri": "http://localhost:1111/receive",
            "method": "POST",
            "body": {
              "port": "${workflow.input.port}",
              "type": "${workflow.input.type}",
              "message": "${workflow.input.message}"
            }
          }
        }
      },
      {
      	"type": "HTTP",
        "name": "basic-microservice-2",
        "taskReferenceName": "basic-microservice-2",
        "inputParameters": {
          "http_request": {
            "uri": "http://localhost:2222/receive",
            "method": "POST",
            "body": {
              "port": "${basic-microservice-1.output.response.body.port}",
              "type": "${basic-microservice-1.output.response.body.type}",
              "message": "${basic-microservice-1.output.response.body.message}",
              "trailer": "${basic-microservice-1.output.response.body.trailer}"
            }
          }
        }
      }
    ]
  }
}
  ],
  "schemaVersion": 2
}
